if syntaxcheck then return end

local region = function(startSection, stopSection, dllName)
    local base  = dllName or process
    local start = getAddressSafe(base..startSection)
    local size  = getAddressSafe(base..stopSection) - start - 0x1
    if start and size then return start, size end
end

local multiActive = function(required, state)
    for i = 1, #required do
        if required[i] then required[i].active = state end
    end
end

local start, size = region(".text", ".rdata")

