local req = {
    AddressList["Required.playerMovement"],
    AddressList["Required.itemData"],
    AddressList["Required.FMWorld"],
    AddressList["Required.Find.BaseAddress"]
}

local Register = {}

function Register.enable(author)
    waitForSections(); waitForPDB()
    for key, tbl in pairs(author) do
        local al, state = AddressList[key], Register.multiCustomScan(tbl)
        if al then al.color = state and 0xD58F00 or 0x808080 end
    end
    memrec.color = 0xD58F00
end

function Register.disable(author)
    for key, tbl in pairs(author) do
        local al = AddressList[key]
        if al then al.color = 0x808080 end
        for i = 1, #tbl do local args = tbl[i]; unregisterSymbol(args[1]) end
    end
    memrec.color = 0x808080
end

function Register.multiCustomScan(array)
    local available = true
    for i = 1, #array do
        local args = array[i]
        local addr = Register.customScan(args[2], args[3], args[4], args[5], args[6])
        if not addr then available = false end
        unregisterSymbol(args[1]); registerSymbol(args[1], addr)
    end
    return available
end

function Register.customScan(strAOB, countNum, selectNum, start, size)
    if not (start and size) then return end
    local result = Register.memScan(strAOB, start, size)
    if not result then return end
    if not ((result.count - 1) == countNum) then result.destroy(); return end
    local address = result[selectNum]
    result.destroy(); return address
end

function Register.memScan(strAOB, start, size)
    local ms = createMemScan()
    local fl = createFoundList(ms)
    memscan_firstScan(ms, soExactValue, vtByteArray, nil, strAOB, nil, start, start + size,
    "*X*W", fsmNotAligned, nil, true, false, false, false)
    memscan_waitTillDone(ms); foundlist_initialize(fl)
    if (fl.getCount() < 1) then ms.destroy(); fl.destroy(); return end
    local result = createStringlist()
    for i = 0, foundlist_getCount(fl) do result.add(fl.getAddress(i - 1)) end
    ms.destroy(); fl.destroy(); return result
end

[ENABLE]

Register.enable(DhaosCollider); multiActive(req, true)

[DISABLE]

multiActive(req, false); Register.disable(DhaosCollider)

